def count_evens(s):
    count = 0
    for val in s:
        if val % 2 == 0:
            count += 1
    return count

t = { 1, 2, 4, 7 }

assert(2 in t)
assert(3 not in t)
print(str(count_evens(t)))
t.add(9)
print(str(count_evens(t)))
t.add(10)
print(str(count_evens(t)))


